												return <div key ={i}>
                          <div> {courses[1]}</div>
                        <Assignmentbox assignment={courses.assignment.title} 
                              submitted={assignment.submitted} passed={assignment.passed}/>
												</div>
											})}
							</div>
						);
					})
        } 
        </div>
      </div>  

      <div className="quizes">
          <h1>Quiz and Tests</h1>
          <div className="quizboxes">
              <QuizBox type={"Quiz 1"} status={""}/>
              <QuizBox type={"Test 1"} status={""}/>
              <QuizBox type={"Test 2"} status={""}/>
          </div>
      </div>

    </section>
  );
};


export default Assignments;
