import React, { Component } from "react";
import "./assignments.scss";
import Assignmentbox from "./assignmentbox/assignmentbox";
import QuizBox from "./quizbox/quixbox";
import courseData from "../../jsonData/courses.json";


const Assignments = () => {
  const courses = courseData.courses;

  return (
    <section>
      <div className="assignments">
        <h1>Assignments</h1>
        <div className="assignmentboxes">

      {
          courses.map((courses, index) => {
						return (
							<div key={index==0} >
											{courses.assignments.map(function (assignment, index) { 
												return <div key={index}>
                        <Assignmentbox assignment={assignment.title} 
                              submitted={assignment.submitted} passed={assignment.passed}/>
												</div>
											})}
							</div>
						);
					})
        }  







        </div>
      </div>  

      <div className="quizes">
          <h1>Quiz and Tests</h1>
          <div className="quizboxes">
              <QuizBox type={"Quiz 1"} status={""}/>
              <QuizBox type={"Test 1"} status={""}/>
              <QuizBox type={"Test 2"} status={""}/>
          </div>
      </div>

    </section>
  );
};


export default Assignments;
