import React, { useState } from "react";
import "./courseBox.scss";
import { FaGraduationCap } from "react-icons/fa";
import { IoIosPeople } from "react-icons/io";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const CourseBox = ({
  courseName,
  imgUrl,
  info,
  newCourse,
  id,
  hp,
  participants,
  inputProgress,
}) => {
  const checkIfNewCourse = () => {
    if (newCourse) {
      return (
        <div className="newCourseBox">
          <h4>NEW</h4>
        </div>
      );
    }
  };
  const setProgressbar = (progress) => {
    const percentage = progress;
    return (
      <CircularProgressbar
        value={percentage}
        text={`${percentage}%`}
        styles={buildStyles({


          // Whether to use rounded or flat corners on the ends - can use 'butt' or 'round'
          strokeLinecap: 'butt',
          // Colors
          pathColor: `rgba(62, 152, 199, ${percentage / 100})`,
          textColor: '#f88',
          trailColor: '#d6d6d6',
        })}
      />
    );
  };

  return (
    <a href={"#/course/" + id} className="courseBox">
      <div className="imageBox">
        <img src={process.env.PUBLIC_URL + imgUrl} alt={courseName} />
        {checkIfNewCourse()}
      </div>
      <h2>{courseName}</h2>
      <div className="hp">
        <FaGraduationCap className="graduationCap" />
        <h4>{hp}hp</h4>
      </div>
      <p>{info}</p>
      <div className="participants">
        <IoIosPeople /> <h4>{participants} participants</h4>
      </div>
      <div className="progressBar">{setProgressbar(inputProgress)}</div>
    </a>
  );
};

export default CourseBox;
