      if(corrected){
        if(passed){
          return (
            <div className="passed">
              <h2>Passed</h2>
            </div>
          )
        } else{
          return(
            <div className="notPassed">
              <h2>Not passed</h2>
            </div>
          )
        } 
      } else{
        <div className="submitted">
            <h2>Submitted</h2>
        </div>
      }
    } else{
      return(
        <div className="notSubmitted">
          <h2>Not submitted</h2>
        </div>
      )
    }

  }


  //Add to database if assignment is submitted or not. (Sumbitted text?)
