    //If not passed ... (change text to not passed?)
  return (
      <div>
    <div className="assignmentBox">
        {/*  Add image? */}
        {checkStatus()}

    </div>

  <h4>{assignment} </h4>
      </div>


  );
};

export default Assignmentbox;
